import React, { useState } from 'react';
import '../css/BuscarImovel.css';
import logo from '../img/logo.png';
import { useNavigate } from 'react-router-dom';

function BuscarImovel() {
  const [ufValue, setUfValue] = useState('');
  const [cityOptions, setCityOptions] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [bairroValue, setBairroValue] = useState('');
  const [tipoValue, setTipoValue] = useState('');
  const [quartosValue, setQuartosValue] = useState('');
  const [banheirosValue, setBanheirosValue] = useState('');
  const [mostrarResultados, setMostrarResultados] = useState(false); // Estado para controlar a exibição dos resultados
  const navigate = useNavigate();

  const handleUfChange = (event) => {
    const selectedUf = event.target.value;
    setUfValue(selectedUf);
    setCityOptions(getCityOptions(selectedUf));
    setSelectedCity('');
  };

  const handleCityChange = (event) => {
    const selectedCity = event.target.value;
    setSelectedCity(selectedCity);
  };

  const handleBairroChange = (event) => {
    const selectedBairro = event.target.value;
    setBairroValue(selectedBairro);
  };

  const handleTipoChange = (event) => {
    const selectedTipo = event.target.value;
    setTipoValue(selectedTipo);
  };

  const handleQuartosChange = (event) => {
    const selectedQuartos = event.target.value;
    setQuartosValue(selectedQuartos);
  };

  const handleBanheirosChange = (event) => {
    const selectedBanheiros = event.target.value;
    setBanheirosValue(selectedBanheiros);
  };

  const getCityOptions = (selectedUf) => {
    const cityOptionsMap = {
      PR: ['Curitiba', 'Londrina', 'Maringá', 'Foz do Iguaçu', 'Cascavel'],
    };
    return cityOptionsMap[selectedUf] || [];
  };

  const bairroOptionsMap = {
    Uberaba: 'Uberaba',
    'Prado Velho': 'Prado Velho',
    'Água Verde': 'Água Verde',
  };

  const tipoOptions = ['Casa', 'Apartamento'];

  const [sliderValue, setSliderValue] = useState(50);
  const [numericValue, setNumericValue] = useState(50);
  const [maxSliderValue, setMaxSliderValue] = useState(10000);
  const [maxNumericValue, setMaxNumericValue] = useState(10000);

  const handleSliderChange = (event) => {
    const value = event.target.value;
    setSliderValue(value);
    setNumericValue(value);
  };

  const handleNumericInputChange = (event) => {
    const value = event.target.value;
    setNumericValue(value);
    setSliderValue(value);
  };

  const handleMaxSliderChange = (event) => {
    const value = event.target.value;
    setMaxSliderValue(value);
    setMaxNumericValue(value);
  };

  const handleMaxNumericInputChange = (event) => {
    const value = event.target.value;
    setMaxNumericValue(value);
    setMaxSliderValue(value);
  };

  const [selectedRow, setSelectedRow] = useState(null);

  const rows = [
    { ufCidade: 'PR - Curitiba', bairro: 'Uberaba', tipo: 'Casa', quartos: 3, banheiros: 2, valor: 'R$800.00' },
    { ufCidade: 'PR - Curitiba', bairro: 'Prado Velho', tipo: 'Apto.', quartos: 2, banheiros: 1, valor: 'R$1200.00' },
    { ufCidade: '', bairro: '', tipo: '', quartos: null, banheiros: null, valor: '' },
    { ufCidade: '', bairro: '', tipo: '', quartos: null, banheiros: null, valor: '' },
  ];

  const handleRowClick = (index) => {
    setSelectedRow(index);
  };

  const handleBuscarImovel = () => {
    // Inserir a lógica para buscar os imóveis com base nos filtros selecionados
    setMostrarResultados(true);
  };

  const verImoClick = () => {
    navigate('/component/VisualizarImovel.js');
  };

  return (
    <div className="BuscarImovel">
      <header className="BuscarImovel-header">
        <table>
          <tbody>
            <tr>
              <td colSpan="3">
                <img src={logo} className="rentfyLogoBuscarImovel" alt="logo" />
              </td>
            </tr>
            <tr>
              <td colSpan="3">Busque um Imóvel</td>
            </tr>
            <tr>
              <td>
                <fieldset className="fieldset-custom">
                  <legend>Filtros</legend>
                  <form id="GerenciarImovel">
                    <div className="form-group">
                      <label htmlFor="cep">CEP</label>
                      <br />
                      <input type="text" id="cep" className="rounded-input" pattern="[0-9]{5}-?[0-9]{3}" />
                    </div>
                    <div className="form-group">
                      <label htmlFor="estado">Estado</label>
                      <br />
                      <select id="estado" className="rounded-input" onChange={handleUfChange}>
                        <option value="">Selecione um estado</option>
                        <option value="AC">Acre</option>
                        <option value="AL">Alagoas</option>
                        <option value="AP">Amapá</option>
                        <option value="AM">Amazonas</option>
                        <option value="BA">Bahia</option>
                        <option value="CE">Ceará</option>
                        <option value="DF">Distrito Federal</option>
                        <option value="ES">Espírito Santo</option>
                        <option value="GO">Goiás</option>
                        <option value="MA">Maranhão</option>
                        <option value="MT">Mato Grosso</option>
                        <option value="MS">Mato Grosso do Sul</option>
                        <option value="MG">Minas Gerais</option>
                        <option value="PA">Pará</option>
                        <option value="PB">Paraíba</option>
                        <option value="PR">Paraná</option>
                        <option value="PE">Pernambuco</option>
                        <option value="PI">Piauí</option>
                        <option value="RJ">Rio de Janeiro</option>
                        <option value="RN">Rio Grande do Norte</option>
                        <option value="RS">Rio Grande do Sul</option>
                        <option value="RO">Rondônia</option>
                        <option value="RR">Roraima</option>
                        <option value="SC">Santa Catarina</option>
                        <option value="SP">São Paulo</option>
                        <option value="SE">Sergipe</option>
                        <option value="TO">Tocantins</option>
                      </select>
                    </div>
                    <div className="form-group">
                      <label htmlFor="cidade">Cidade</label>
                      <br />
                      <select id="cidade" className="rounded-input" value={selectedCity} onChange={handleCityChange}>
                        <option value="">Selecione uma cidade</option>
                        {cityOptions.map((city) => (
                          <option key={city} value={city}>
                            {city}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div className="form-group">
                      <label htmlFor="bairro">Bairro</label>
                      <br />
                      <select id="bairro" className="rounded-input" value={bairroValue} onChange={handleBairroChange}>
                        <option value="">Selecione um bairro</option>
                        {Object.keys(bairroOptionsMap).map((bairro) => (
                          <option key={bairro} value={bairro}>
                            {bairroOptionsMap[bairro]}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div className="form-group">
                      <label htmlFor="tipo">Tipo</label>
                      <br />
                      <select id="tipo" className="rounded-input" value={tipoValue} onChange={handleTipoChange}>
                        <option value="">Selecione um tipo</option>
                        {tipoOptions.map((tipo) => (
                          <option key={tipo} value={tipo}>
                            {tipo}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div className="form-group">
                      <label htmlFor="quartos">Quartos</label>
                      <br />
                      <input
                        type="number"
                        id="quartos"
                        className="rounded-input"
                        value={quartosValue}
                        onChange={handleQuartosChange}
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="banheiros">Banheiros</label>
                      <br />
                      <input
                        type="number"
                        id="banheiros"
                        className="rounded-input"
                        value={banheirosValue}
                        onChange={handleBanheirosChange}
                      />
                    </div>
                  </form>
                  <button className="btnFiltrarImovel" onClick={handleBuscarImovel}>
                    Buscar Imóvel
                  </button>
                </fieldset>
              </td>
            </tr>
            <tr>
              <td colSpan="3">
                {mostrarResultados && (
                  <>
                    <p>Imóveis Localizados</p>
                    <br />
                    <table id="imoveis-localizados" className="table-bordered" style={{ fontSize: '16px' }}>
                      <thead>
                        <tr>
                          <th>UF - Cidade</th>
                          <th>Bairro</th>
                          <th>Tipo</th>
                          <th>Quartos</th>
                          <th>Banheiros</th>
                          <th>Valor</th>
                        </tr>
                      </thead>
                      <tbody>
                        {rows.map((row, index) => (
                          <tr
                            key={index}
                            onClick={() => handleRowClick(index)}
                            style={{ backgroundColor: selectedRow === index ? 'lightblue' : '' }}
                          >
                            <td>{row.ufCidade}</td>
                            <td>{row.bairro}</td>
                            <td>{row.tipo}</td>
                            <td>{row.quartos}</td>
                            <td>{row.banheiros}</td>
                            <td>{row.valor}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                    <br />
                    <button className="btnCadastrarImovel" onClick={verImoClick}>
                      Visualizar Imóvel
                    </button>
                  </>
                )}
              </td>
            </tr>
          </tbody>
        </table>
      </header>
    </div>
  );
}

export default BuscarImovel;
