import React, { useState } from 'react';
import '../css/CadastroImovel.css';
import logo from '../img/logo.png';

function CadastroImovel() {
  const [cities, setCities] = useState([]);
  const [selectedUF, setSelectedUF] = useState('');

  const populateCities = () => {
    const ufSelect = document.getElementById("UFs");
    const selectedUF = ufSelect.value;
    setSelectedUF(selectedUF);

    if (selectedUF !== "") {
        // Obter as cidades do estado selecionado (usando a base dos Correios)
        getCitiesByUF(selectedUF)
          .then((cities) => setCities(cities))
          .catch((error) => {
            console.error("Erro ao obter as cidades:", error);
            setCities([]);
          });
      } else {
        // Limpar as cidades se nenhum UF for selecionado
        setCities([]);
      }
  };

  const getCitiesByUF = async (uf) => {
    try {
      const response = await fetch("https://www.correios.com.br/assets/docs/estrutura-territorial/tabela-cep/relacao-uf-localidade.csv");
      const csvContent = await response.text();
  
      // Processar o conteúdo do arquivo CSV
      const lines = csvContent.split("\n");
  
      const cities = [];
  
      for (let i = 1; i < lines.length; i++) {
        const line = lines[i].trim();
  
        // Extrair o UF e a cidade da linha do CSV
        const values = line.split(",");
        const csvUF = values[0].replace(/"/g, "").trim();
        const csvCity = values[1].replace(/"/g, "").trim();
  
        if (csvUF === uf) {
          cities.push(csvCity);
        }
      }
  
      return cities;
    } catch (error) {
      console.error("Erro ao obter as cidades:", error);
      return [];
    }
  };
  

  return (
    <div className="CadastroImovel">
      <header className="CadastroImovel-header">
        <table>
          <tbody>
            <tr>
              <td colSpan="2">
                <img src={logo} className="rentfyLogoCadastroImovel" alt="logo" />
              </td>
            </tr>
            <tr>
              <td colSpan="2">Cadastre seu Imóvel</td>
            </tr>
            <tr>
              <td>
                <fieldset>
                  <legend>Informações do Imóvel</legend>
                  <label htmlFor="UF" className="labelCadImo">UF: </label>
                  <select name="UFs" id="UFs" onChange={populateCities}>
                    <option value="" disabled selected>--</option>
                    <option value="AC">AC</option>
                    <option value="AL">AL</option>
                    <option value="AP">AP</option>
                    <option value="AM">AM</option>
                    <option value="BA">BA</option>
                    <option value="CE">CE</option>
                    <option value="DF">DF</option>
                    <option value="ES">ES</option>
                    <option value="GO">GO</option>
                    <option value="MA">MA</option>
                    <option value="MT">MT</option>
                    <option value="MS">MS</option>
                    <option value="MG">MG</option>
                    <option value="PA">PA</option>
                    <option value="PB">PB</option>
                    <option value="PR">PR</option>
                    <option value="PE">PE</option>
                    <option value="PI">PI</option>
                    <option value="RJ">RJ</option>
                    <option value="RN">RN</option>
                    <option value="RS">RS</option>
                    <option value="RO">RO</option>
                    <option value="RR">RR</option>
                    <option value="SC">SC</option>
                    <option value="SP">SP</option>
                    <option value="SE">SE</option>
                    <option value="TO">TO</option>
                  </select>
                  &nbsp;&nbsp;
                  <label htmlFor="Cidade" className="labelCadImo">Cidade: </label>
                  <select id="cidadeSelect" name="cidade">
                    <option value="">Selecione um UF primeiro</option>
                    {cities.map((city, index) => (
                      <option key={index} value={city}>
                        {city}
                      </option>
                    ))}
                  </select>
                </fieldset>
              </td>
              <td>
                <p>Localização do Imóvel</p>
              </td>
            </tr>
          </tbody>
        </table>
      </header>
    </div>
  );
}

export default CadastroImovel;
